import{_ as a,c as i,o as r,ag as o}from"./chunks/framework.Cycwnjj0.js";const s="/assets/ens01.ITSaeMja.jpg",n="/assets/ens02.yb3G_T_Q.jpg",t="/assets/27001.BwW1ubbp.png",d="/assets/nist01.CParIi9k.png",l="/assets/cisomindmap.CkW_cmBO.png",_=JSON.parse('{"title":"Gestión de la Seguridad de la Información","description":"","frontmatter":{},"headers":[],"relativePath":"03_gestion/index.md","filePath":"03_gestion/index.md","lastUpdated":null}'),c={name:"03_gestion/index.md"};function u(p,e,m,g,f,h){return r(),i("div",null,[...e[0]||(e[0]=[o('<h1 id="gestion-de-la-seguridad-de-la-informacion" tabindex="-1">Gestión de la Seguridad de la Información <a class="header-anchor" href="#gestion-de-la-seguridad-de-la-informacion" aria-label="Permalink to &quot;Gestión de la Seguridad de la Información&quot;">​</a></h1><nav class="table-of-contents"><ul><li><a href="#esquema-nacional-de-seguridad-para-organizaciones-publicas-en-espana">Esquema Nacional de Seguridad (para organizaciones públicas en España)</a><ul><li><a href="#politica-de-seguridad-de-la-informacion-psi">Política de Seguridad de la Información (PSI)</a></li><li><a href="#responsabilidades-y-funciones">Responsabilidades y funciones</a></li><li><a href="#inventario-de-activos">Inventario de activos</a></li><li><a href="#categorizacion-de-los-sistemas">Categorización de los sistemas</a></li><li><a href="#declaracion-de-aplicabilidad">Declaración de aplicabilidad</a></li><li><a href="#analisis-y-gestion-de-riesgos">Análisis y gestión de riesgos</a></li><li><a href="#normativa-de-seguridad">Normativa de seguridad</a></li><li><a href="#gestion-de-personal">Gestión de personal</a></li><li><a href="#plan-de-formacion-y-concienciacion">Plan de formación y concienciación</a></li><li><a href="#gestion-incidentes-de-seguridad">Gestión Incidentes de seguridad</a></li><li><a href="#adquisicion-de-productos-de-seguridad-y-contratacion-de-servicios-de-seguridad">Adquisición de productos de seguridad y contratación de servicios de seguridad</a></li><li><a href="#listado-de-expedientes-de-aapp-relacionados-con-ciberseguridad">Listado de expedientes de AAPP relacionados con ciberseguridad</a></li><li><a href="#clausulado-de-seguridad-en-la-contratacion-publica">Clausulado de Seguridad en la contratación pública</a></li><li><a href="#auditorias-de-cumplimiento">Auditorías de Cumplimiento</a></li></ul></li><li><a href="#iso-27000">ISO 27000</a></li><li><a href="#nist-cibersecurity-framework">NIST Cibersecurity Framework</a></li><li><a href="#cis-critical-security-controls">CIS (Critical Security Controls)</a><ul><li><a href="#basics-1-6">Basics (1-6)</a></li><li><a href="#foundational-7-16">Foundational (7-16)</a></li><li><a href="#organizational-17-20">Organizational (17-20)</a></li><li><a href="#recursos-interesantes-sobre-cis-critital-security-controls">Recursos interesantes sobre CIS (Critital Security Controls)</a></li></ul></li><li><a href="#secure-controls-framework">Secure Controls Framework</a></li><li><a href="#cuestiones-generales-sobre-organizacion-de-la-ciberseguridad">Cuestiones generales sobre organización de la Ciberseguridad</a></li></ul></nav><h2 id="esquema-nacional-de-seguridad-para-organizaciones-publicas-en-espana" tabindex="-1">Esquema Nacional de Seguridad (para organizaciones públicas en España) <a class="header-anchor" href="#esquema-nacional-de-seguridad-para-organizaciones-publicas-en-espana" aria-label="Permalink to &quot;Esquema Nacional de Seguridad (para organizaciones públicas en España)&quot;">​</a></h2><p>El <a href="https://www.boe.es/buscar/act.php?id=BOE-A-2010-1330" target="_blank" rel="noreferrer">Real Decreto 3/2010</a>, de 8 de enero, por el que se regula el Esquema Nacional de Seguridad en el ámbito de la Administración Electrónica da cumplimiento a lo previsto en el artículo 42 de la Ley 11/2007, de 22 de junio, de acceso electrónico de los ciudadanos a los Servicios Públicos. Su objeto es establecer la política de seguridad en la utilización de medios electrónicos y está constituido por principios básicos y requisitos mínimos que permitan una protección adecuada de la información. Por tanto, la finalidad del Esquema Nacional de Seguridad es la creación de las condiciones necesarias de confianza en el uso de los medios electrónicos, a través de medidas para garantizar la seguridad de los sistemas, los datos, las comunicaciones y los servicios electrónicos, que permita a los ciudadanos y a las Administraciones públicas, el ejercicio de derechos y el cumplimiento de deberes a través de estos medios.</p><p><img src="'+s+'" alt="IMG"></p><p><a href="https://www.ccn-cert.cni.es/publico/ens/ens/index.html#!1073" target="_blank" rel="noreferrer">Detalle de los 75 controles</a></p><p>La adecuación ordenada al Esquema Nacional de Seguridad requiere el tratamiento de diversas cuestiones:</p><ul><li>Preparar y aprobar la política de seguridad, incluyendo la definición de roles y la asignación de responsabilidades. (Véase <a href="https://www.ccn-cert.cni.es/publico/seriesCCN-STIC/series/800-Esquema_Nacional_de_Seguridad/805-Politica_de_seguridad_del_ENS/805-ENS_politica-sep11.pdf" target="_blank" rel="noreferrer">CCN-STIC 805 Política de seguridad de la información</a>)</li><li>Categorizar los sistemas atendiendo a la valoración de la información manejada y de los servicios prestados.(Véase <a href="https://www.ccn-cert.cni.es/publico/seriesCCN-STIC/series/800-Esquema_Nacional_de_Seguridad/803-Valoracion_en_el_ENS/803_ENS-valoracion_ene-11.pdf" target="_blank" rel="noreferrer">CCN-STIC 803 - Valoración de sistemas en el Esquema Nacional de Seguridad</a>)</li><li>Realizar el análisis de riesgos, incluyendo la valoración de las medidas de seguridad existentes. (Véase <a href="http://administracionelectronica.gob.es/ctt/magerit" target="_blank" rel="noreferrer">Magerit versión 3</a> y <a href="https://www.ccn-cert.cni.es/ens/instrumentos-para-la-adecuacion.html" target="_blank" rel="noreferrer">programas de apoyo –Pilar-</a> )</li><li>Preparar y aprobar la Declaración de aplicabilidad de las medidas del Anexo II del ENS. (Véase <a href="https://www.ccn-cert.cni.es/publico/seriesCCN-STIC/series/800-Esquema_Nacional_de_Seguridad/804-Medidas_de_implantacion_del_ENS/804_medidas_de_implantacion_del_ens.pdf" target="_blank" rel="noreferrer">CCN-STIC 804 Medidas e implantación del Esquema Nacional de Seguridad</a>)</li><li>Elaborar un plan de adecuación para la mejora de la seguridad, sobre la base de las insuficiencias detectadas, incluyendo plazos estimados de ejecución. (Véase <a href="https://www.ccn-cert.cni.es/publico/seriesCCN-STIC/series/800-Esquema_Nacional_de_Seguridad/806-Plan_adecuacion_ENS/806_ENS-adecuacion_ene-11.pdf" target="_blank" rel="noreferrer">CCN-STIC 806 Plan de adecuación del Esquema Nacional de Seguridad</a>) Implantar operar y monitorizar las medidas de seguridad a través de la gestión continuada de la seguridad correspondiente. (Véase serie <a href="https://www.ccn-cert.cni.es/index.php?option=com_content&amp;view=article&amp;id=3639&amp;Itemid=246&amp;lang=es" target="_blank" rel="noreferrer">CCN-STIC</a>)</li><li>Auditar la seguridad (Véase <a href="https://www.ccn-cert.cni.es/publico/seriesCCN-STIC/series/800-Esquema_Nacional_de_Seguridad/802-Auditoria_ENS/802-Auditoria_ENS-jun10.pdf" target="_blank" rel="noreferrer">CCN-STIC 802 Auditoría del Esquema Nacional de Seguridad</a> y <a href="https://www.ccn-cert.cni.es/publico/seriesCCN-STIC/series/800-Esquema_Nacional_de_Seguridad/808/808-Verificacion_del_cumplimiento_medidas_ENS-sep11.pdf" target="_blank" rel="noreferrer">CCN-STIC 808 Verificación del cumplimiento de las medidas en el Esquema Nacional de Seguridad</a>)</li><li>Informar sobre el estado de la seguridad (Véase <a href="https://www.ccn-cert.cni.es/publico/seriesCCN-STIC/series/800-Esquema_Nacional_de_Seguridad/815-Metricas_e_Indicadores_en_el_ENS/815_Metricas_e_indicadores_en_el_ENS-feb14.pdf" target="_blank" rel="noreferrer">CCN-STIC 815 Métricas e Indicadores en el Esquema Nacional de Seguridad</a> y <a href="https://www.ccn-cert.cni.es/series-ccn-stic/800-guia-esquema-nacional-de-seguridad/542-ccn-stic-824-informaci%C3%B3n-del-estado-de-seguridad/file.html" target="_blank" rel="noreferrer">CCN-STIC 824 Informe del Estado de Seguridad</a>) <img src="'+n+'" alt="IMG"></li></ul><p><a href="https://www.ccn-cert.cni.es/publico/dmpublidocuments/ENS-FAQ.pdf" target="_blank" rel="noreferrer">Preguntas habituales sobre el ENS</a></p><h3 id="politica-de-seguridad-de-la-informacion-psi" tabindex="-1">Política de Seguridad de la Información (PSI) <a class="header-anchor" href="#politica-de-seguridad-de-la-informacion-psi" aria-label="Permalink to &quot;Política de Seguridad de la Información (PSI)&quot;">​</a></h3><p>La seguridad deberá comprometer a todos los miembros de la organización. La <strong>política de seguridad (PSI)</strong> deberá identificar unos claros responsables de velar por su cumplimiento y ser conocida por todos los miembros de la organización administrativa.</p><p><a href="https://www.ccn-cert.cni.es/series-ccn-stic/800-guia-esquema-nacional-de-seguridad/508-ccn-stic-805-politica-de-seguridad-de-la-informacion/file.html" target="_blank" rel="noreferrer">Guía CCN-STIC sobre Políticas de Seguridad</a></p><p>Se pueden consultar las Políticas de Seguridad de la información en el listado del <a href="https://administracionelectronica.gob.es/pae_Home/pae_Estrategias/pae_Seguridad_Inicio/Politicas_de_seguridad.html#.Xzf0KTXtb-g" target="_blank" rel="noreferrer">PAe</a></p><p>PSIs de <strong>ministerios</strong>:</p><ul><li><a href="http://www.boe.es/boe/dias/2015/05/29/pdfs/BOE-A-2015-5937.pdf" target="_blank" rel="noreferrer">Ministerio de Agricultura, Alimentación y Medio Ambiente</a></li><li><a href="http://www.boe.es/buscar/doc.php?id=BOE-A-2013-9544" target="_blank" rel="noreferrer">Ministerio de Asuntos Exteriores y de Cooperación</a></li><li><a href="https://www.boe.es/eli/es/o/2019/12/27/cud1313" target="_blank" rel="noreferrer">Ministerio de Cultura y Deporte</a></li><li><a href="http://www.belt.es/legislacion/vigente/Seg_inf/Seg_inf/estatal/290506_OM_Seg_Informacion.pdf" target="_blank" rel="noreferrer">Ministerio de Defensa</a></li><li><a href="http://sede.sepe.gob.es/es/portaltrabaja/resources/sede/pdf/OM_politica_seguridad.pdf" target="_blank" rel="noreferrer">Ministerio de Empleo y Seguridad Social</a></li><li><a href="http://www.boe.es/buscar/doc.php?id=BOE-A-2014-11118" target="_blank" rel="noreferrer">Ministerio de Fomento</a></li><li><a href="http://www.boe.es/boe/dias/2014/10/27/pdfs/BOE-A-2014-10965.pdf" target="_blank" rel="noreferrer">Ministerio de Hacienda y Administraciones Públicas</a></li><li><a href="http://www.boe.es/diario_boe/txt.php?id=BOE-A-2014-10732" target="_blank" rel="noreferrer">Ministerio de Industria, Turismo y Comercio</a></li><li><a href="https://www.boe.es/buscar/doc.php?id=BOE-A-2019-5536" target="_blank" rel="noreferrer">Ministerio de Interior</a></li><li><a href="https://www.boe.es/boe/dias/2017/12/28/pdfs/BOE-A-2017-15662.pdf" target="_blank" rel="noreferrer">Ministerio de Justicia</a></li><li><a href="http://www.boe.es/diario_boe/txt.php?id=BOE-A-2011-18187" target="_blank" rel="noreferrer">Ministerio de Política Territorial y Administración Pública</a></li><li><a href="http://sedempr.gob.es/es/content/normativa-reguladora" target="_blank" rel="noreferrer">Ministerio de Presidencia</a></li><li><a href="http://www.boe.es/buscar/doc.php?id=BOE-A-2014-2378" target="_blank" rel="noreferrer">Ministerio de Sanidad, Servicios Sociales e Igualdad</a></li></ul><p>PSIs de <strong>organismos públicos</strong> de la AGE:</p><ul><li><a href="http://www.boe.es/buscar/act.php?id=BOE-A-2012-14621" target="_blank" rel="noreferrer">AEAT</a> (Agencia Estatal de Administración Tributaria)</li><li><a href="https://www.cnmc.es/sites/default/files/2018-06-18_Resolucion_Pol%C3%ADtica_de_Seguridad_de_la_Informaci%C3%B3n.pdf" target="_blank" rel="noreferrer">Comisión Nacional de los Mercados y la Competencia</a> (CNMC)</li><li><a href="http://boe.es/diario_boe/txt.php?id=BOE-A-2016-165" target="_blank" rel="noreferrer">IGAE</a> - Secretaría de Estado de Presupuestos y Gastos y de la Intervención General de la Administración del Estado</li><li><a href="https://www.red.es/redes/sites/redes/files/Poli%CC%81tica%20de%20Seguridad.pdf" target="_blank" rel="noreferrer">Red.es</a></li><li><a href="https://sede.seg-social.gob.es/wps/portal/sede/sede/Inicio/NormativayLegislacion" target="_blank" rel="noreferrer">Seguridad Social</a></li></ul><p>PSIs de <strong>Comunidades Autónomas</strong> y sus organismos:</p><ul><li>Aragón: <a href="https://www.aragon.es/-/politica-de-seguridad-de-la-informacion-del-departamento-de-agricultura-ganaderia-y-medio-ambiente" target="_blank" rel="noreferrer">Departamento de Agricultura, Ganadería y Medio Ambiente</a>, <a href="https://www.iisaragon.es/wp-content/uploads/2019/09/POLPoliticaSeguridadInformacion1A.pdf" target="_blank" rel="noreferrer">Instituto de Investigación Sanitaria</a></li><li>Madrid: <a href="http://www.madrid.org/wleg_pub/secure/normativas/contenidoNormativa.jsf?opcion=VerHtml&amp;idnorma=9364&amp;word=S&amp;wordperfect=N&amp;pdf=S#no-back-button" target="_blank" rel="noreferrer">Consejería de Sanidad</a></li><li>Andalucía: <a href="http://www.juntadeandalucia.es/boja/2011/11/2" target="_blank" rel="noreferrer">Junta de Andalucía</a>.<a href="http://www.juntadeandalucia.es/boja/2017/110/BOJA17-110-00009-10321-01_00115562.pdf" target="_blank" rel="noreferrer">Modificación de 2017</a></li><li>La Rioja: <a href="http://ias1.larioja.org/boletin/Bor_Boletin_visor_Servlet?referencia=1755989-1-PDF-483969" target="_blank" rel="noreferrer">Comunidad Autónoma de La Rioja</a></li></ul><p>PSIs de <strong>entidades locales</strong>:</p><ul><li><a href="http://anleo.jgpa.es:8080/documentos/Boletines/PDF/10C-029.pdf" target="_blank" rel="noreferrer">Junta General del Principado de Asturias</a></li><li><a href="http://www.malaga.eu/export/sites/default/ayto/portadaAyto/portal/menu/portada/documentos/Politica_de_Seguridad_de_la_Informacion_Ayuntamiento_de_Malaga.pdf" target="_blank" rel="noreferrer">Ayuntamiento de Málaga</a></li></ul><h3 id="responsabilidades-y-funciones" tabindex="-1">Responsabilidades y funciones <a class="header-anchor" href="#responsabilidades-y-funciones" aria-label="Permalink to &quot;Responsabilidades y funciones&quot;">​</a></h3><ul><li><a href="https://www.ccn-cert.cni.es/series-ccn-stic/800-guia-esquema-nacional-de-seguridad/501-ccn-stic-801-responsibilidades-y-funciones-en-el-ens/file.html" target="_blank" rel="noreferrer">CCN-STIC-801</a> Responsabilidades y Funciones</li></ul><h3 id="inventario-de-activos" tabindex="-1">Inventario de activos <a class="header-anchor" href="#inventario-de-activos" aria-label="Permalink to &quot;Inventario de activos&quot;">​</a></h3><p>El inventario de activos conforma el primer elemento de la cadena en un sistema de gestión de la seguridad de un sistema. Un inventario de activos se define como una lista de todos aquellos recursos (físicos, software, documentos, servicios, personas, instalaciones, etc.) que tengan valor para la organización y necesiten por tanto ser protegidos de potenciales riesgos. Dicho de otra manera, si no sabes qué tienes que proteger ¿cómo vas a protegerlo?</p><p><a href="https://www.incibe-cert.es/blog/inventario-activos-y-gestion-seguridad-sci" target="_blank" rel="noreferrer">Inventario de activos según INCIBE</a></p><p>¿Qué herramientas te pueden ayudar con el inventario de activos?</p><ul><li>Identity and systems management tools</li><li>Endpoint security management software</li><li>Vulnerability scanning tools</li><li>Passive and active network monitoring solutions</li><li>Cloud orchestration technologies</li></ul><h3 id="categorizacion-de-los-sistemas" tabindex="-1">Categorización de los sistemas <a class="header-anchor" href="#categorizacion-de-los-sistemas" aria-label="Permalink to &quot;Categorización de los sistemas&quot;">​</a></h3><p><a href="https://www.ccn-cert.cni.es/series-ccn-stic/800-guia-esquema-nacional-de-seguridad/682-ccn-stic-803-valoracion-de-sistemas-en-el-ens-1/file.html" target="_blank" rel="noreferrer">CCN-STIC-803</a> ENS. Valoración de los sistemas</p><h3 id="declaracion-de-aplicabilidad" tabindex="-1">Declaración de aplicabilidad <a class="header-anchor" href="#declaracion-de-aplicabilidad" aria-label="Permalink to &quot;Declaración de aplicabilidad&quot;">​</a></h3><p>La Declaración de Aplicabilidad, en el ámbito del <a href="https://www.boe.es/buscar/act.php?id=BOE-A-2010-1330" target="_blank" rel="noreferrer">ENS</a>, es el documento en el que se <strong>formaliza la relación de medidas de seguridad</strong> que resultan de aplicación al sistema de información de que se trate, conforme a su categoría, y que se encuentran recogidas en el <a href="https://www.boe.es/buscar/act.php?id=BOE-A-2010-1330#anii" target="_blank" rel="noreferrer">Anexo II del RD 3/2010</a>, que lo regula.</p><p>Las medidas de seguridad referenciadas en el Anexo II <strong>podrán ser reemplazadas por otras medidas compensatorias</strong> siempre y cuando se justifique documentalmente que protegen igual o mejor el riesgo sobre los activos (<a href="https://www.boe.es/buscar/act.php?id=BOE-A-2010-1330#ani" target="_blank" rel="noreferrer">Anexo I</a>) y se satisfacen los principios básicos y los requisitos mínimos previstos en los capítulos II y III del Real Decreto 3/2010.</p><p>Como parte integral de la Declaración de Aplicabilidad, se indicará de forma detallada la correspondencia entre las medidas compensatorias implementadas y las medidas del Anexo II que compensan siendo objeto de aprobación formal por parte del responsable de seguridad.</p><p>Referencias:</p><ul><li><a href="./.html"></a></li><li><a href="https://www.ccn-cert.cni.es/informes/informes-de-buenas-practicas-bp/3830-ccn-cert-bp-14-declaracion-de-aplicacibilidad-ens/file.html" target="_blank" rel="noreferrer">CCN-CERT BP/14</a> - Declaración de Aplicabilidad en el ENS(Perfil de Cumplimiento).</li></ul><h3 id="analisis-y-gestion-de-riesgos" tabindex="-1">Análisis y gestión de riesgos <a class="header-anchor" href="#analisis-y-gestion-de-riesgos" aria-label="Permalink to &quot;Análisis y gestión de riesgos&quot;">​</a></h3><p>Cada organización que desarrolle e implante sistemas para el tratamiento de la información y las comunicaciones realizará su propia gestión de riesgos.</p><p>Esta gestión se realizará por medio del análisis y tratamiento de los riesgos a los que está expuesto el sistema. Las medidas adoptadas para mitigar o suprimir los riesgos deberán estar justificadas y, en todo caso, existirá una <strong>proporcionalidad</strong> entre ellas y los riesgos.</p><p>Referencias:</p><ul><li><a href="https://administracionelectronica.gob.es/pae_Home/pae_Documentacion/pae_Metodolog/pae_Magerit.html" target="_blank" rel="noreferrer">Magerit v3</a> Metodología para el análisis y gestión de riesgos.</li><li><a href="https://pilar.ccn-cert.cni.es/" target="_blank" rel="noreferrer">PILAR</a> Conjunto de soluciones del CCN para la realización del análisis de riesgos.</li></ul><h3 id="normativa-de-seguridad" tabindex="-1">Normativa de seguridad <a class="header-anchor" href="#normativa-de-seguridad" aria-label="Permalink to &quot;Normativa de seguridad&quot;">​</a></h3><p>Guías CCN-STIC:</p><ul><li><a href="https://www.ccn-cert.cni.es/series-ccn-stic/800-guia-esquema-nacional-de-seguridad/529-ccn-stic-821-normas-de-seguridad-en-el-ens/file.html" target="_blank" rel="noreferrer">ENS. Normas de seguridad</a></li><li><a href="https://www.ccn-cert.cni.es/publico/seriesCCN-STIC/series/800-Esquema_Nacional_de_Seguridad/821-Normas_de_Seguridad_en_el_ENS/821-Normas_de_seguridad-abr13-NG00.pdf" target="_blank" rel="noreferrer">ENS. Apéndice I. NG 00 Utilización de recursos y sistemas</a></li><li><a href="https://www.ccn-cert.cni.es/series-ccn-stic/800-guia-esquema-nacional-de-seguridad/531-ccn-stic-821-normas-de-seguridad-en-el-ens-anexo-ii/file.html" target="_blank" rel="noreferrer">ENS. Apéndice II. NP 10 Normas de acceso a Internet</a></li><li><a href="https://www.ccn-cert.cni.es/publico/seriesCCN-STIC/series/800-Esquema_Nacional_de_Seguridad/821-Normas_de_Seguridad_en_el_ENS/821-Normas_de_seguridad-abr13-NP20.pdf" target="_blank" rel="noreferrer">ENS. Apéndice III. NP 20 Normas de acceso al correo Electrónico (E-Mail)</a></li><li><a href="https://www.ccn-cert.cni.es/publico/seriesCCN-STIC/series/800-Esquema_Nacional_de_Seguridad/821-Normas_de_Seguridad_en_el_ENS/821-Normas_de_seguridad-abr13-NP30.pdf" target="_blank" rel="noreferrer">ENS. Apéndice IV. NP 30 Normas para trabajar fuera de las instalaciones del organismo</a></li><li><a href="https://www.ccn-cert.cni.es/series-ccn-stic/800-guia-esquema-nacional-de-seguridad/534-ccn-stic-821-normas-de-seguridad-en-el-ens-anexo-v/file.html" target="_blank" rel="noreferrer">ENS. Apéndice V. NP 40 Normas de creación y uso de contraseñas</a></li><li><a href="https://www.ccn-cert.cni.es/series-ccn-stic/800-guia-esquema-nacional-de-seguridad/535-ccn-stic-821-normas-de-seguridad-en-el-ens-anexo-vi/file.html" target="_blank" rel="noreferrer">ENS. Apéndice VI. NP 50 Acuerdo de confidencialidad para terceros</a></li><li><a href="https://www.ccn-cert.cni.es/series-ccn-stic/800-guia-esquema-nacional-de-seguridad/536-ccn-stic-821-normas-de-seguridad-en-el-ens-anexo-vii/file.html" target="_blank" rel="noreferrer">ENS. Apéndice VII. NP 60 Modelo de contenido de buenas prácticas para terceros</a></li><li><a href="https://www.ccn-cert.cni.es/series-ccn-stic/800-guia-esquema-nacional-de-seguridad/2722-821-ccn-stic-apendice-viii/file.html" target="_blank" rel="noreferrer">ENS. Apéndice VIII. NP 70 Normativa de uso de redes sociales</a></li></ul><h3 id="gestion-de-personal" tabindex="-1">Gestión de personal <a class="header-anchor" href="#gestion-de-personal" aria-label="Permalink to &quot;Gestión de personal&quot;">​</a></h3><p>Todo el personal relacionado con la información y los sistemas deberá ser formado e informado de sus deberes y obligaciones en materia de seguridad. Sus actuaciones deben ser supervisadas para verificar que se siguen los procedimientos establecidos.</p><p>El personal relacionado con la información y los sistemas, ejercitará y aplicará los principios de seguridad en el desempeño de su cometido.</p><p>El significado y alcance del uso seguro del sistema se concretará y plasmará en unas normas de seguridad.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Cada usuario que acceda a la información del sistema debe estar <strong>identificado de forma única</strong></p></div><h3 id="plan-de-formacion-y-concienciacion" tabindex="-1">Plan de formación y concienciación <a class="header-anchor" href="#plan-de-formacion-y-concienciacion" aria-label="Permalink to &quot;Plan de formación y concienciación&quot;">​</a></h3><p>Pendiente de desarrollar</p><h3 id="gestion-incidentes-de-seguridad" tabindex="-1">Gestión Incidentes de seguridad <a class="header-anchor" href="#gestion-incidentes-de-seguridad" aria-label="Permalink to &quot;Gestión Incidentes de seguridad&quot;">​</a></h3><p>Referencias:</p><ul><li><a href="https://www.ccn-cert.cni.es/series-ccn-stic/800-guia-esquema-nacional-de-seguridad/988-ccn-stic-817-gestion-de-ciberincidentes/file.html" target="_blank" rel="noreferrer">ENS Gestión de ciberincidentes</a></li><li><a href="http://www.interior.gob.es/documents/10180/9771228/Gu%C3%ADa+Nacional+de+Notificaci%C3%B3n+y+Gesti%C3%B3n+de+Ciberincidentes.pdf" target="_blank" rel="noreferrer">GUÍA NACIONAL DE NOTIFICACIÓN Y GESTIÓN DE CIBERINCIDENTES</a></li></ul><h3 id="adquisicion-de-productos-de-seguridad-y-contratacion-de-servicios-de-seguridad" tabindex="-1">Adquisición de productos de seguridad y contratación de servicios de seguridad <a class="header-anchor" href="#adquisicion-de-productos-de-seguridad-y-contratacion-de-servicios-de-seguridad" aria-label="Permalink to &quot;Adquisición de productos de seguridad y contratación de servicios de seguridad&quot;">​</a></h3><p>En la adquisición de productos de seguridad de las TIC que vayan a ser empleados por las AAPP se utilizarán, de forma proporcionada a la categoría del sistema y nivel de seguridad determinados, aquellos que tengan <strong>certificada la funcionalidad de seguridad</strong> relacionada con el objeto de su adquisición, salvo en aquellos casos en que las exigencias de proporcionalidad en cuanto a los riesgos asumidos no lo justifiquen a juicio del responsable de Seguridad. Ver <a href="./../new_04_tecnologias/#catalogo-de-productos-de-seguridad-de-las-tecnologias-de-la-informacion-y-la-comunicacion">Catálogo CCN-STIC</a></p><h3 id="listado-de-expedientes-de-aapp-relacionados-con-ciberseguridad" tabindex="-1">Listado de expedientes de AAPP relacionados con ciberseguridad <a class="header-anchor" href="#listado-de-expedientes-de-aapp-relacionados-con-ciberseguridad" aria-label="Permalink to &quot;Listado de expedientes de AAPP relacionados con ciberseguridad&quot;">​</a></h3><ul><li>GISS 2023: <a href="https://contrataciondelestado.es/wps/portal/!ut/p/b0/04_Sj9CPykssy0xPLMnMz0vMAfIjU1JTC3Iy87KtUlJLEnNyUuNzMpMzSxKTgQr0w_Wj9KMyU1zLcvQjfZJKkyojPH19w3IjAsMMwl3NcyIDym1t9Qtycx0BKOGFLQ!!/" target="_blank" rel="noreferrer">Servicios de ciberseguridad</a></li><li>GISS 2023: <a href="https://contrataciondelestado.es/wps/portal/!ut/p/b0/DcoxCoAwDADABzkExElwUFHoVJy0WSS0UYKxdqh9v44HBwgbYKQiJ2V5IulvF5iTSrzawJlUeVfxksn_AVZAQAlTUXCVYVubB8fZvUdDg63KokvXQbrv_gMl7p7Y/" target="_blank" rel="noreferrer">CASB/proxy nube</a></li><li>GISS 2023: <a href="https://contrataciondelestado.es/wps/portal/!ut/p/b0/04_Sj9CPykssy0xPLMnMz0vMAfIjU1JTC3Iy87KtUlJLEnNyUuNzMpMzSxKTgQr0w_Wj9KMyU1zLcvQjiwrcPd0cy5J8Pf3MMyxMVQ0KcnOL08ptbfWBDEcAVgXhmA!!/" target="_blank" rel="noreferrer">SIEM</a></li><li>Andalucia CERT 2023: <a href="https://www.juntadeandalucia.es/haciendayadministracionpublica/apl/pdc_sirec/perfiles-licitaciones/detalle-licitacion.jsf?idExpediente=595121" target="_blank" rel="noreferrer">Servicios de SOC</a></li><li>Andalucia CERT 2023: <a href="https://www.juntadeandalucia.es/haciendayadministracionpublica/apl/pdc_sirec/perfiles-licitaciones/detalle-licitacion.jsf?idExpediente=455736" target="_blank" rel="noreferrer">EDR y servicios asociados</a> Andalucia CERT 2023: <a href="https://www.juntadeandalucia.es/haciendayadministracionpublica/apl/pdc_sirec/perfiles-licitaciones/detalle-licitacion.jsf?idExpediente=626420" target="_blank" rel="noreferrer">Acuderdo marco servicios ciber</a></li><li>JCCM 2023: <a href="https://contrataciondelestado.es/wps/portal/!ut/p/b0/04_Sj9CPykssy0xPLMnMz0vMAfIjU1JTC3Iy87KtUlJLEnNyUuNzMpMzSxKTgQr0w_Wj9KMyU1zLcvQjfb3zXEKNfN3yXcw8nD2Ls8q8jIsyAm1t9Qtycx0Bsr_Axg!!/" target="_blank" rel="noreferrer">Servicios de SOC</a></li><li>CCMAD 2023: <a href="https://contrataciondelestado.es/wps/portal/!ut/p/b0/DcoxCoAwDADAJ4U6Ch1EfICLrVkk2qDBtCrUor_X8eAAwQMmKrJSliOR_h4D86mS9jpwJlWeVBbJtPwBHCCghK4ojL56jRtK30gbr8rczzbT0FsLZ4zNB2vB0rA!/" target="_blank" rel="noreferrer">Servicios de ciberseguridad</a></li></ul><h3 id="clausulado-de-seguridad-en-la-contratacion-publica" tabindex="-1">Clausulado de Seguridad en la contratación pública <a class="header-anchor" href="#clausulado-de-seguridad-en-la-contratacion-publica" aria-label="Permalink to &quot;Clausulado de Seguridad en la contratación pública&quot;">​</a></h3><p>Pendiente de desarrollar.</p><p>Referencias interesantes:</p><ul><li><a href="https://www.ccn.cni.es/index.php/es/docman/documentos-publicos/abstract/257-abstract-obligaciones-de-los-prestadores-de-servicios-a-las-entidades-publicas/file" target="_blank" rel="noreferrer">Obligaciones de los prestadores de servicios a las entidades públicas</a></li><li><a href="https://www.enisa.europa.eu/publications/good-practices-for-the-security-of-healthcare-services/at_download/fullReport" target="_blank" rel="noreferrer">Guía para la contratación de productos y servicios de seguridad en entornos hospitalarios. ENISA</a></li></ul><h3 id="auditorias-de-cumplimiento" tabindex="-1">Auditorías de Cumplimiento <a class="header-anchor" href="#auditorias-de-cumplimiento" aria-label="Permalink to &quot;Auditorías de Cumplimiento&quot;">​</a></h3><ul><li><a href="https://www.ccn-cert.cni.es/series-ccn-stic/800-guia-esquema-nacional-de-seguridad/502-ccn-stic-802-auditoria-del-ens/file.html" target="_blank" rel="noreferrer">CCN-STIC-802</a> ENS. Guía de auditoría.</li><li><a href="https://www.ccn-cert.cni.es/series-ccn-stic/800-guia-esquema-nacional-de-seguridad/518-ccn-stic-808-verificacion-del-cumplimiento-de-las-medidas-en-el-ens-borrador/file.html" target="_blank" rel="noreferrer">CCN-STIC-808</a> Verificación del cumplimiento del ENS</li></ul><h2 id="iso-27000" tabindex="-1">ISO 27000 <a class="header-anchor" href="#iso-27000" aria-label="Permalink to &quot;ISO 27000&quot;">​</a></h2><p><a href="https://es.wikipedia.org/wiki/ISO/IEC_27000-series" target="_blank" rel="noreferrer">Serie 27000</a><a href="https://www.iso.org/isoiec-27001-information-security.html" target="_blank" rel="noreferrer">ISO/IEC 27001</a><img src="'+t+'" alt="ISO 27001"></p><ul><li><a href="https://www.iso.org/standard/76559.html" target="_blank" rel="noreferrer">ISO/IEC 27018:2019 Protección de datos personales en la nube</a></li><li><a href="https://www.iso.org/standard/71670.html" target="_blank" rel="noreferrer">ISO/IEC 27701:2019 Gestión de la información de privacidad</a></li><li><a href="https://www.iso.org/standard/70331.html" target="_blank" rel="noreferrer">ISO/IEC 29184:2020 Aviso de privacidad online y consentimiento</a></li><li><a href="https://www.iso.org/standard/72436.html" target="_blank" rel="noreferrer">ISO/IEC 27102:2019 Gestión de seguridad de la información – Guía para ciberseguros</a></li><li><a href="https://www.iso.org/standard/68242.html" target="_blank" rel="noreferrer">ISO/IEC 19086-4:2019 SLA Cloud computing–Componentes de seguridad y protección de datos personales</a></li><li><a href="https://www.iso.org/standard/75106.html" target="_blank" rel="noreferrer">ISO 22301:2019 Continuidad del negocio</a></li></ul><h2 id="nist-cibersecurity-framework" tabindex="-1">NIST Cibersecurity Framework <a class="header-anchor" href="#nist-cibersecurity-framework" aria-label="Permalink to &quot;NIST Cibersecurity Framework&quot;">​</a></h2><p><a href="https://nvlpubs.nist.gov/nistpubs/CSWP/NIST.CSWP.04162018.pdf" target="_blank" rel="noreferrer">Framework for Improving Critical Infrastructure Cybersecurity</a><a href="https://www.nist.gov/document/cybersecurityframeworkv1-1presentationpptx" target="_blank" rel="noreferrer">Presentación</a><img src="'+d+'" alt="NIST CSF"></p><p><a href="https://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-37r2.pdf" target="_blank" rel="noreferrer">NIST SP 800-37 Risk Management Framework Rev 2</a></p><h2 id="cis-critical-security-controls" tabindex="-1">CIS (Critical Security Controls) <a class="header-anchor" href="#cis-critical-security-controls" aria-label="Permalink to &quot;CIS (Critical Security Controls)&quot;">​</a></h2><p>Extraido de <a href="http://www.cisecurity.org/controls/" target="_blank" rel="noreferrer">Center For Internet Security</a>, con licencia Creative Commons Attribution-Non-Commercial-No Derivatives 4.0 International Public License.</p><p><a href="/docs/cis-controls-v71.pdf">PDF</a> CIS Security Controls v7.1 <a href="/docs/cis-controls-v71.xlsx.html">Excel</a> CIS Security Controls v7.1</p><h3 id="basics-1-6" tabindex="-1">Basics (1-6) <a class="header-anchor" href="#basics-1-6" aria-label="Permalink to &quot;Basics (1-6)&quot;">​</a></h3><h4 id="_1-inventario-y-control-de-activos-de-hardware" tabindex="-1">1. Inventario y control de activos de hardware <a class="header-anchor" href="#_1-inventario-y-control-de-activos-de-hardware" aria-label="Permalink to &quot;1. Inventario y control de activos de hardware&quot;">​</a></h4><p>Administración activa (inventario, seguimiento y corrección) de todos los dispositivos de hardware en la red para que solo los dispositivos autorizados tengan acceso, y aquellos dispositivos que no estén autorizados ni gestionados, sean localizados y se les impide el acceso.</p><h4 id="_2-inventario-y-control-de-activos-de-software" tabindex="-1">2. Inventario y control de activos de software <a class="header-anchor" href="#_2-inventario-y-control-de-activos-de-software" aria-label="Permalink to &quot;2. Inventario y control de activos de software&quot;">​</a></h4><p>Administración activa (inventariar, rastrear y corregir) de todo el software en la red para que solo el software autorizado está instalado y puede ejecutarse, y que todo el software no autorizado y no administrado, sea localizado y se impide su instalación o ejecución.</p><h4 id="_3-gestion-continua-de-vulnerabilidades" tabindex="-1">3. Gestión continua de vulnerabilidades <a class="header-anchor" href="#_3-gestion-continua-de-vulnerabilidades" aria-label="Permalink to &quot;3. Gestión continua de vulnerabilidades&quot;">​</a></h4><p>Obtener, evaluar y tomar acciones continuamente sobre nueva información para identificar vulnerabilidades, remediar y minimizar la ventana de oportunidad para los atacantes.</p><h4 id="_4-uso-controlado-de-privilegios-administrativos" tabindex="-1">4. Uso controlado de privilegios administrativos <a class="header-anchor" href="#_4-uso-controlado-de-privilegios-administrativos" aria-label="Permalink to &quot;4. Uso controlado de privilegios administrativos&quot;">​</a></h4><p>Los procesos y herramientas utilizados para rastrear / controlar / prevenir / corregir el uso, asignación y configuración de privilegios administrativos en equipos, redes y aplicaciones.</p><h4 id="_5-configuracion-segura-de-hardware-y-software-en-dispositivos-moviles-equipos-portatiles-puestos-de-trabajo-y-servidores" tabindex="-1">5. Configuración segura de hardware y software en dispositivos móviles, equipos portátiles, puestos de trabajo y servidores <a class="header-anchor" href="#_5-configuracion-segura-de-hardware-y-software-en-dispositivos-moviles-equipos-portatiles-puestos-de-trabajo-y-servidores" aria-label="Permalink to &quot;5. Configuración segura de hardware y software en dispositivos móviles, equipos portátiles, puestos de trabajo y servidores&quot;">​</a></h4><p>Establecer, implementar y administrar activamente (realizar un seguimiento, informar y corregir) la configuración de seguridad de dispositivos móviles, portátiles, servidores y puestos de trabajo con una configuración rigurosa de gestión y control de cambios, para evitar que los atacantes exploten servicios y entornos vulnerables.</p><h4 id="_6-mantenimiento-seguimiento-y-analisis-de-registros-de-auditoria" tabindex="-1">6. Mantenimiento, seguimiento y análisis de registros de auditoría <a class="header-anchor" href="#_6-mantenimiento-seguimiento-y-analisis-de-registros-de-auditoria" aria-label="Permalink to &quot;6. Mantenimiento, seguimiento y análisis de registros de auditoría&quot;">​</a></h4><p>Recopilar, administrar y analizar los registros de auditoría de eventos que podrían ayudar a detectar, comprender o recuperarse de un ataque.</p><h3 id="foundational-7-16" tabindex="-1">Foundational (7-16) <a class="header-anchor" href="#foundational-7-16" aria-label="Permalink to &quot;Foundational (7-16)&quot;">​</a></h3><h4 id="_7-protecciones-de-correo-electronico-y-navegador-web" tabindex="-1">7.Protecciones de correo electrónico y navegador web <a class="header-anchor" href="#_7-protecciones-de-correo-electronico-y-navegador-web" aria-label="Permalink to &quot;7.Protecciones de correo electrónico y navegador web&quot;">​</a></h4><p>Minimizar la superficie de ataque y las oportunidades para que los atacantes manipulen el comportamiento humano, a través de su interacción con navegadores web y sistemas de correo electrónico</p><h4 id="_8-defensas-contra-malware" tabindex="-1">8. Defensas contra malware <a class="header-anchor" href="#_8-defensas-contra-malware" aria-label="Permalink to &quot;8. Defensas contra malware&quot;">​</a></h4><p>Controlar la instalación, propagación y ejecución de código malicioso en varios puntos de la organización, mientras se optimiza el uso de la automatización para permitir una actualización rápida de los datos de defensa, recolección y acción correctiva.</p><h4 id="_9-limitacion-y-control-de-puertos-de-red-protocolos-y-servicios" tabindex="-1">9. Limitación y control de puertos de red, Protocolos y servicios <a class="header-anchor" href="#_9-limitacion-y-control-de-puertos-de-red-protocolos-y-servicios" aria-label="Permalink to &quot;9. Limitación y control de puertos de red, Protocolos y servicios&quot;">​</a></h4><p>Gestionar (rastrear / controlar / corregir) el uso operativo continuo de puertos, protocolos y servicios.</p><h4 id="_10-capacidades-de-recuperacion-de-datos" tabindex="-1">10. Capacidades de recuperación de datos <a class="header-anchor" href="#_10-capacidades-de-recuperacion-de-datos" aria-label="Permalink to &quot;10. Capacidades de recuperación de datos&quot;">​</a></h4><p>Los procesos y herramientas utilizados para respaldar adecuadamente la información crítica con una metodología probada. para la recuperación oportuna de la misma.</p><h4 id="_11-configuracion-segura-para-dispositivos-de-red-como-cortafuegos-enrutadores-y-conmutadores" tabindex="-1">11. Configuración segura para dispositivos de red, como cortafuegos, enrutadores y conmutadores <a class="header-anchor" href="#_11-configuracion-segura-para-dispositivos-de-red-como-cortafuegos-enrutadores-y-conmutadores" aria-label="Permalink to &quot;11. Configuración segura para dispositivos de red, como cortafuegos, enrutadores y conmutadores&quot;">​</a></h4><p>Establecer, implementar y administrar activamente (rastrear, informar, corregir) la configuración de seguridad de Dispositivos de infraestructura de red que utilizan una gestión rigurosa de configuración y control de cambios, para evitar que los atacantes exploten servicios y configuraciones vulnerables.</p><h4 id="_12-proteccion-perimetral" tabindex="-1">12. Protección perimetral <a class="header-anchor" href="#_12-proteccion-perimetral" aria-label="Permalink to &quot;12. Protección perimetral&quot;">​</a></h4><p>Detectar / prevenir / corregir el flujo de información que se transfiere a través de redes de diferente nivel de confianza con un enfoque en datos puedan ser dañinos para la seguridad.</p><h4 id="_13-proteccion-de-datos" tabindex="-1">13. Protección de Datos <a class="header-anchor" href="#_13-proteccion-de-datos" aria-label="Permalink to &quot;13. Protección de Datos&quot;">​</a></h4><p>Los procesos y herramientas utilizados para prevenir la filtración de datos, mitigar los efectos de la filtración de datos, y garantizar la privacidad e integridad de la información confidencial.</p><h4 id="_14-acceso-controlado-basado-en-la-necesidad-de-saber" tabindex="-1">14. Acceso controlado basado en la necesidad de saber <a class="header-anchor" href="#_14-acceso-controlado-basado-en-la-necesidad-de-saber" aria-label="Permalink to &quot;14. Acceso controlado basado en la necesidad de saber&quot;">​</a></h4><p>Los procesos y herramientas utilizados para rastrear / controlar / prevenir / corregir el acceso seguro a activos críticos (por ejemplo, información, recursos, sistemas) de acuerdo con la determinación formal de qué personas, que equipos y qué aplicaciones tienen la necesidad y el derecho de acceder a estos activos críticos basado en una clasificación aprobada.</p><h4 id="_15-control-de-acceso-inalambrico" tabindex="-1">15. Control de acceso inalámbrico <a class="header-anchor" href="#_15-control-de-acceso-inalambrico" aria-label="Permalink to &quot;15. Control de acceso inalámbrico&quot;">​</a></h4><p>Los procesos y herramientas utilizados para rastrear / controlar / prevenir / corregir el uso seguro del área local inalámbrica redes (WLAN), puntos de acceso y sistemas de clientes inalámbricos.</p><h4 id="_16-seguimiento-y-control-de-cuentas" tabindex="-1">16. Seguimiento y control de cuentas <a class="header-anchor" href="#_16-seguimiento-y-control-de-cuentas" aria-label="Permalink to &quot;16. Seguimiento y control de cuentas&quot;">​</a></h4><p>Administrar activamente el ciclo de vida de las cuentas de aplicaciones y sistemas - su creación, uso, inactividad, eliminación - con el fin de minimizar las oportunidades de que los atacantes los aprovechen.</p><h3 id="organizational-17-20" tabindex="-1">Organizational (17-20) <a class="header-anchor" href="#organizational-17-20" aria-label="Permalink to &quot;Organizational (17-20)&quot;">​</a></h3><h4 id="_17-implementar-un-programa-de-capacitacion-y-concienciacion-sobre-seguridad" tabindex="-1">17. Implementar un programa de capacitación y concienciación sobre seguridad <a class="header-anchor" href="#_17-implementar-un-programa-de-capacitacion-y-concienciacion-sobre-seguridad" aria-label="Permalink to &quot;17. Implementar un programa de capacitación y concienciación sobre seguridad&quot;">​</a></h4><p>Para todos los roles funcionales en la organización (priorizando aquellos de misión crítica para el negocio y su seguridad), identificar los conocimientos, habilidades y habilidades específicos necesarios para apoyar la defensa de la organización; desarrollar y ejecutar un plan integrado para evaluar, identificar brechas y remediar a través de programas de políticas, planificación organizacional, capacitación y concienciación, en materia de seguridad.</p><h4 id="_18-seguridad-del-software-de-aplicacion" tabindex="-1">18. Seguridad del software de aplicación <a class="header-anchor" href="#_18-seguridad-del-software-de-aplicacion" aria-label="Permalink to &quot;18. Seguridad del software de aplicación&quot;">​</a></h4><p>Gestionar el ciclo de vida de seguridad de todo el software desarrollado y adquirido internamente para prevenir, detectar y corregir las debilidades de seguridad.</p><h4 id="_19-gestion-y-respuesta-a-incidentes" tabindex="-1">19. Gestión y respuesta a incidentes <a class="header-anchor" href="#_19-gestion-y-respuesta-a-incidentes" aria-label="Permalink to &quot;19. Gestión y respuesta a incidentes&quot;">​</a></h4><p>Proteger la información de la organización, así como su reputación, desarrollando e implementando una infraestructura de respuesta a incidentes (por ejemplo, planes, roles definidos, capacitación, comunicaciones, supervisión de la gestión) para descubrir rápidamente un ataque y luego contener eficazmente el daño, erradicar la presencia del atacante y restaurar la integridad de la red y sistemas.</p><h4 id="_20-pruebas-de-penetracion-y-ejercicios-de-redteam" tabindex="-1">20. Pruebas de penetración y ejercicios de RedTeam <a class="header-anchor" href="#_20-pruebas-de-penetracion-y-ejercicios-de-redteam" aria-label="Permalink to &quot;20. Pruebas de penetración y ejercicios de RedTeam&quot;">​</a></h4><p>Poner a prueba la solidez general de la defensa de una organización (la tecnología, los procesos y las personas) simulando los objetivos y acciones de un atacante.</p><h3 id="recursos-interesantes-sobre-cis-critital-security-controls" tabindex="-1">Recursos interesantes sobre CIS (Critital Security Controls) <a class="header-anchor" href="#recursos-interesantes-sobre-cis-critital-security-controls" aria-label="Permalink to &quot;Recursos interesantes sobre CIS (Critital Security Controls)&quot;">​</a></h3><ul><li><a href="https://www.auditscripts.com/download/2788/" target="_blank" rel="noreferrer">AuditScripts Critical Security Control Executive Assessment Tool</a></li><li><a href="https://www.auditscripts.com/download/4229/" target="_blank" rel="noreferrer">CIS Critical Security Control v7.1 Assessment Tool</a></li><li><a href="https://www.auditscripts.com/download/2742/" target="_blank" rel="noreferrer">AuditScripts Critical Security Controls Master Mapping</a></li><li><a href="https://www.sans.org/reading-room/whitepapers/hsoffice/small-business-budget-implementation-20-security-controls-33744" target="_blank" rel="noreferrer">2011. A Small Business No Budget Implementation of the SANS 20 Security Controls</a></li></ul><h2 id="secure-controls-framework" tabindex="-1">Secure Controls Framework <a class="header-anchor" href="#secure-controls-framework" aria-label="Permalink to &quot;Secure Controls Framework&quot;">​</a></h2><p><a href="https://www.securecontrolsframework.com/" target="_blank" rel="noreferrer">https://www.securecontrolsframework.com/</a></p><h2 id="cuestiones-generales-sobre-organizacion-de-la-ciberseguridad" tabindex="-1">Cuestiones generales sobre organización de la Ciberseguridad <a class="header-anchor" href="#cuestiones-generales-sobre-organizacion-de-la-ciberseguridad" aria-label="Permalink to &quot;Cuestiones generales sobre organización de la Ciberseguridad&quot;">​</a></h2><p>CISO Mind Map by <a href="https://rafeeqrehman.com/2020/06/12/ciso-mindmap-2020-what-do-infosec-professionals-really-do/" target="_blank" rel="noreferrer">RAFEEQ REHMAN</a><a href="http://rafeeqrehman.com/wp-content/uploads/2020/06/CISO_Job_MindMap_Rafeeq_Rehman_v_2020.pdf" target="_blank" rel="noreferrer">PDF</a></p><p><img src="'+l+'" alt="IMG"></p>',123)])])}const w=a(c,[["render",u]]);export{_ as __pageData,w as default};
